import * as React from 'react';
import Paper from '@mui/material/Paper';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TablePagination from '@mui/material/TablePagination';
import TableRow from '@mui/material/TableRow';
import axios from 'axios';
import {useEffect, useState} from 'react';

interface Column {
  id: keyof City;  
  label: string;
  minWidth?: number;
  align?: 'right';
  format?: (value: number) => string;
}

const columns: readonly Column[] = [
  { id: 'id',
    label: 'ID', 
    minWidth: 50, 
    align: 'right',
    format: (value: number) => value.toLocaleString('en-US'),
  },
  { id: 'name',
    label: 'Name', 
    minWidth: 170, 
    align: 'right'
  },
  {
    id: 'area',
    label: 'Area',
    minWidth: 170,
    align: 'right',
    format: (value: number) => value.toLocaleString('en-US'),
  },
  {
    id: 'population',
    label: 'Population',
    minWidth: 170,
    align: 'right',
    format: (value: number) => value.toLocaleString('en-US'),
  },
  {
    id: 'metersAboveSeaLevel',
    label: 'Meters Above Sea Level',
    minWidth: 170,
    align: 'right',
    format: (value: number) => value.toLocaleString('en-US'),
  },
  {
    id: 'government',
    label: 'Government',
    minWidth: 170,
    align: 'right',
  },
  {
    id: 'governor',
    label: 'Governor',
    minWidth: 170,
    align: 'right',
  },
];

interface City {
    id: string;
    name: string;
    coordinates: Coordinates;
    creationDate: Date;
    area: number;
    population?: number;
    establishmentDate?: Date;
    metersAboveSeaLevel?: number;
    capital: boolean;
    government: Government;
    governor: Human;
}


interface Coordinates {
    x?: number;
    y: number;
}

enum Government {
    DESPOTISM = "DESPOTISM",
    PATRIARCHY = "PATRIARCHY",
    ETHNOCRACY = "ETHNOCRACY",
}

interface Human{
    height?: number;
    birthday?: Date;
}

const createCity = (
    id: string,
    name: string,
    coordinates: Coordinates,
    creationDate: Date,
    area: number,
    capital: boolean,
    government: Government,
    governor: Human,
    population?: number,
    metersAboveSeaLevel?: number,
): City => ({
    id,
    name,
    coordinates,
    creationDate,
    area,
    population,
    metersAboveSeaLevel,
    capital,
    government,
    governor,
});

const formatValue = (value: any) => {
    if (value === null || value === undefined) {
        return 'N/A'; // Обработка null и undefined
    }

    if (typeof value === 'number') {
        return value.toLocaleString('en-US'); // Форматирование чисел
    } else if (typeof value === 'boolean') {
        return value ? 'Yes' : 'No'; // Преобразование булевых значений
    } else if (value instanceof Date) {
        return value.toLocaleDateString(); // Форматирование дат
    } else if (typeof value === 'object') {
        // Пример для Coordinates
        if ('x' in value && 'y' in value) {
            return `(${value.x ?? 0}, ${value.y})`; // Формат для Coordinates
        }
        // Пример для Human
        if ('height' in value) {
            return `${value.height ?? 'N/A'} cm`; // Формат для Human
        }
        return JSON.stringify(value); // Превратите объекты в строки
    }

    return String(value); // Преобразование других типов в строку
};


const cities: City[] = [
    createCity('1', 'Sample City', { x: 10, y: 20 }, new Date(), 250, true, Government.DESPOTISM, { height: 180 }, 500000, 300),
    createCity('2', 'Another City', { y: 30 }, new Date(), 550, false, Government.PATRIARCHY, { height: 175 }, undefined, undefined),
    // Добавьте больше городов по мере необходимости
];

export default function TableCity() {
    const [cities, setCities] = useState<City[]>([]);
    const [page, setPage] = React.useState(0);
    const [rowsPerPage, setRowsPerPage] = React.useState(10);

  const handleChangePage = (event: unknown, newPage: number) => {
    setPage(newPage);
  };

  const handleChangeRowsPerPage = (event: React.ChangeEvent<HTMLInputElement>) => {
    setRowsPerPage(+event.target.value);
    setPage(0);
  };

  useEffect(() => {
    const fetchCities = async () => {
        const requestData = `<?xml version="1.0" encoding="UTF-8"?>
<CitiesRequest>
<page>${page + 1}</page>
<pageSize>${rowsPerPage}</pageSize>
<SortingStrategy>
    <sortingType>asc</sortingType>
    <sortingColumn>id</sortingColumn>
</SortingStrategy>
<FilterStrategy>
    <filterColumn>id</filterColumn>
    <filterType>contains</filterType>
    <filterValue>string</filterValue>
</FilterStrategy>
</CitiesRequest>`;

        try {
            const response = await axios.post('http://localhost:9991/api/city', requestData, {
                headers: {
                    'Content-Type': 'application/xml',
                },
            });

            const parser = new DOMParser();
            const xmlDoc = parser.parseFromString(response.data, "application/xml");
            const fetchedCities: City[] = Array.from(xmlDoc.getElementsByTagName('City')).map(cityElement => ({
                id: cityElement.getElementsByTagName('id')[0].textContent || '',
                name: cityElement.getElementsByTagName('name')[0].textContent || '',
                coordinates: {
                    x: Number(cityElement.getElementsByTagName('x')[0].textContent),
                    y: Number(cityElement.getElementsByTagName('y')[0].textContent),
                },
                creationDate: new Date(cityElement.getElementsByTagName('creationDate')[0].textContent),
                area: Number(cityElement.getElementsByTagName('area')[0].textContent),
                population: Number(cityElement.getElementsByTagName('population')[0].textContent),
                metersAboveSeaLevel: Number(cityElement.getElementsByTagName('metersAboveSeaLevel')[0].textContent),
                capital: cityElement.getElementsByTagName('capital')[0].textContent === 'true',
                government: cityElement.getElementsByTagName('government')[0].textContent as Government,
                governor: Array.from(cityElement.getElementsByTagName('Human')).map(humanElement => ({
                    height: Number(humanElement.getElementsByTagName('height')[0].textContent),
                    birthday: new Date(humanElement.getElementsByTagName('birthday')[0].textContent),
                })),
                humans: Array.from(cityElement.getElementsByTagName('Human')).map(humanElement => ({
                    height: Number(humanElement.getElementsByTagName('height')[0].textContent),
                    birthday: new Date(humanElement.getElementsByTagName('birthday')[0].textContent),
                })),
            }));

            setCities(fetchedCities);
        } catch (error) {
            console.error("Error fetching cities:", error);
        }
    };

    fetchCities();
}, [page, rowsPerPage]);






  return (
    <Paper sx={{ width: '100%', overflow: 'hidden' }}>
      <TableContainer sx={{ maxHeight: 440 }}>
        <Table stickyHeader aria-label="sticky table">
          <TableHead>
            <TableRow>
              {columns.map((column) => (
                <TableCell
                  key={column.id}
                  align={column.align}
                  style={{ minWidth: column.minWidth }}
                >
                  {column.label}
                </TableCell>
              ))}
            </TableRow>
          </TableHead>
          <TableBody>
            {cities
                .slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
                .map((city) => {
                    return (
                        <TableRow hover role="checkbox" tabIndex={-1} key={city.id}>
                            {columns.map((column) => {
                                const value = city[column.id];
                                return (
                                    <TableCell key={column.id} align={column.align}>
                                        {formatValue(value)} {/* Вызов функции форматирования здесь */}
                                    </TableCell>
                                );
                            })}
                        </TableRow>
                    );
                })}
        </TableBody>
        </Table>
      </TableContainer>
      <TablePagination
        rowsPerPageOptions={[10, 25, 100]}
        component="div"
        count={cities.length}
        rowsPerPage={rowsPerPage}
        page={page}
        onPageChange={handleChangePage}
        onRowsPerPageChange={handleChangeRowsPerPage}
      />
    </Paper>
  );
}
