import * as React from 'react';
import Paper from '@mui/material/Paper';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TablePagination from '@mui/material/TablePagination';
import TableRow from '@mui/material/TableRow';
import axios from 'axios';
import {useEffect, useState} from 'react';
import Input from "../Components/Input";
import GetCityButton from "./GetCityButton";
import { City } from '../models/City'; 
import Button from '@mui/material/Button';
import Stack from '@mui/material/Stack';
import Modal from '../Modal/modal';
import GetMinIdCityButton from './GetMinIdCityButton';
import GetMaxDateCityButton from './GetMaxDateCityButton';
import GetGovernmentButton from './GetGovernmentButton';
import Selects from './Selects';
import ButtonGroup from '@mui/material/ButtonGroup';
import '../App.css';
import { Human } from '../models/Human';
import GetDeportButton from './GetDeportButton';
import GetKillButton from './GetKillButton';

interface Column {
  id: keyof City;  
  label: string;
  minWidth?: number;
  align?: 'left';
  format?: (value: number) => string;
}

interface ColumnHuman {
  id: keyof Human;  
  label: string;
  minWidth?: number;
  align?: 'left';
  format?: (value: number) => string;
}

const columnsHuman: readonly ColumnHuman[] = [
  {
    id: 'height',
    label: 'Height',
    minWidth: 30,
    align: 'left',
    format: (value: number) => value.toLocaleString('en-US'),
  },
  {
    id: 'birthday',
    label: 'Birthday',
    minWidth: 30,
    align: 'left',
  },
]

const columns: readonly Column[] = [
  { id: 'id',
    label: 'ID', 
    minWidth: 20, 
    align: 'left',
    format: (value: number) => value.toLocaleString('en-US'),
  },
  { id: 'name',
    label: 'Name', 
    minWidth: 60, 
    align: 'left'
  },
  {
    id: 'area',
    label: 'Area',
    minWidth: 50,
    align: 'left',
    format: (value: number) => value.toLocaleString('en-US'),
  },
  {
    id: 'coordinates',
    label: 'Coordinates',
    minWidth: 100,
    align: 'left',
    format: (value: number) => value.toLocaleString('en-US'),
  },
  {
    id: 'population',
    label: 'Population',
    minWidth: 60,
    align: 'left',
    format: (value: number) => value.toLocaleString('en-US'),
  },
  {
    id: 'capital',
    label: 'Capital',
    minWidth: 40,
    align: 'left',
  },

  {
    id: 'metersAboveSeaLevel',
    label: 'Sea Level',
    minWidth: 40,
    align: 'left',
    format: (value: number) => value.toLocaleString('en-US'),
  },
  {
    id: 'government',
    label: 'Government',
    minWidth: 70,
    align: 'left',
  },

  {
    id: 'creationDate',
    label: 'Creation',
    minWidth: 70,
    align: 'left',
  },
  {
    id: 'establishmentDate',
    label: 'Establishment',
    minWidth: 70,
    align: 'left',
  },
];


const formatValue = (value: any) => {
    if (value === null || value === undefined) {
        return 'N/A'; 
    }

    if (typeof value === 'number') {
        return value.toLocaleString('en-US'); 
    } else if (typeof value === 'boolean') {
        return value ? 'Yes' : 'No'; 
    } else if (value instanceof Date) {
        return value.toLocaleDateString(); 
    } else if (typeof value === 'object') {
        if ('x' in value && 'y' in value) {
            return `(${value.x ?? 0}, ${value.y})`; 
        }
        if ('height' in value) {
            return `${value.height ?? 'N/A'} cm`; 
        }
        return JSON.stringify(value); 
    }   
    return String(value); 
};

const TableCity: React.FC = () => {
  const [cities, setCities] = useState<City[]>([]);
  const [humans, setHumans] = useState<Human[]>([]);
  const [page, setPage] = React.useState(0);
  const [rowsPerPage, setRowsPerPage] = React.useState(100);
  const [inputValue, setInputValue] = useState("");
  const [inputFrom, setInputFrom] = useState("");
  const [inputTo, setInputTo] = useState("");
  const [inputKill, setInputKill] = useState("");
  const [modalActive, setModalActive] = useState(false);
  const [id, setID] = useState('');
  const [name, setName] = useState('');
  const [creationDate, setCreationDatee] = useState(new Date());
  const [area, setArea] = useState(Number);
  const [population, setPopulation] = useState(Number);
  const [metersAboveSeaLevel, setMetersAboveSeaLevel] = useState(Number);
  const [establishmentDate, setEstablishmentDate] = useState<Date | null>(null);
  const [capital, setCapital] = useState(Boolean);
  const [government, setGovernment] = useState("");
  const [x, setX] = useState(Number);
  const [y, setY] = useState(Number);
  const [height, setHeight] = useState(Number);
  const [birthday, setBirthday] = useState<Date | null>(null);
  const [selectGovernment, setSelectGovernment] = React.useState('');

  const handleChange = (value: string) => {
    setSelectGovernment(value);
  };


  const handleCityUpdate = (newCity: City) => {
    setCities([newCity]);
    setHumans(newCity.human)
  };

  const handleCityUpdateList = (newCity: City[]) => {
    setCities(newCity);
    reverseList(newCity);
  };

  const handleHumanUpdate = (newCity: City) => {
    setHumans((prevHumans) => [...prevHumans, ...newCity.human]);
  };

  const handleChangePage = (event: unknown, newPage: number) => {
    setPage(newPage);
    fetchCities();
  };

  const handleChangeRowsPerPage = (event: React.ChangeEvent<HTMLInputElement>) => {
    setRowsPerPage(+event.target.value);
    setPage(0);
    fetchCities();
  };

  const handleInputChange = (value: string) => {
      setInputValue(value);
  };

  const SetData = (value: string) => {
    const index = Number(value) - 1; 
    if (index >= 0 && index < cities.length) {
        const selectedCity = cities[index];
        setID(inputValue);
        setName(selectedCity.name);
        setArea(selectedCity.area);
        setX(selectedCity.coordinates.x ?? 0);
        setY(selectedCity.coordinates.y);
        setPopulation(selectedCity.population ?? 0);
        setCapital(selectedCity.capital);
        setMetersAboveSeaLevel(selectedCity.metersAboveSeaLevel ?? 0);      
        setCreationDatee(new Date(selectedCity.creationDate));
        setEstablishmentDate(selectedCity.establishmentDate ?? null);
        const humans = selectedCity.human;
        humans.map(g => setHeight(g.height ?? 0));
        humans.map(g => setBirthday(g.birthday ?? null));
        setSelectGovernment(selectedCity.government);
    }};
  const reverseList = (city: City[]) => {
    setCities(prevList => [...prevList].reverse()); // Создаем новый массив и переворачиваем его
  };

  const handleButtonClick = async () => {
    try {
      const response = await fetch(`http://localhost:9991/api/cities/${inputValue}`, {
        method: 'DELETE',      
      });
      if (response.ok) {
        console.log('City successfully deleted');
        fetchCities();
      } else if (response.status === 400) {
        console.error("Can't find city with such id");
      } else if (response.status >= 500) {
        console.error('Internal server error');
      }
    } catch (error) {
      console.error('Error deleting city:', error);
    }
  };


  const fetchUpdateCity = async () => {
    const requestDatas = `<?xml version="1.0" encoding="UTF-8"?>
        <CityRequest>
        <name>${name}</name>
        <coordinates>
          <x>${x}</x>
          <y>${y}</y>
        </coordinates>
        <area>${area}</area>
        <population>${population}</population>
        <metersAboveSeaLevel>${metersAboveSeaLevel}</metersAboveSeaLevel>
        <establishmentDate>2024-11-22T01:08:28.794Z</establishmentDate>
        <capital>${capital}</capital>
        <government>${selectGovernment}</government>
        <governor>
          <height>${height}</height>
          <birthday>2024-11-22T01:08:28.794Z</birthday>
        </governor>
      </CityRequest>`;

      const requestData = `<?xml version="1.0" encoding="UTF-8"?>
<CityRequest>
	<name>Новый2</name>
	<coordinates>
		<x>1</x>
		<y>1</y>
	</coordinates>
	<area>1</area>
	<population>1</population>
	<metersAboveSeaLevel>1</metersAboveSeaLevel>
	<establishmentDate>2024-11-22T01:08:28.794Z</establishmentDate>
	<capital>true</capital>
	<government>DESPOTISM</government>
	<governor>
		<height>1</height>
		<birthday>2024-11-22T01:08:28.794Z</birthday>
	</governor>
</CityRequest>`;

        try {
          const response = await fetch(`http://localhost:9991/api/cities/${id}`, {
            method: 'PUT',
                headers: {
                  'Content-Type': 'application/xml',
                },
                body: requestData,
              });

              if (!response.ok) {
                throw new Error('Network response was not ok');
              }        
              fetchCities();
      } catch (error) {
          console.error("Error fetching cities:", error);
      }
    }

    const fetchCreateCities = async () => {
      const requestData = `<?xml version="1.0" encoding="UTF-8"?>
<CityRequest>
  <name>${name}</name>
  <coordinates>
    <x>${x}</x>
    <y>${y}</y>
  </coordinates>
  <area>${area}</area>
  <population>${population}</population>
  <metersAboveSeaLevel>${metersAboveSeaLevel}</metersAboveSeaLevel>
  <establishmentDate>2024-11-22T00:41:50.823Z</establishmentDate>
  <capital>${capital}</capital>
  <government>DESPOTISM</government>
  <governor>
    <height>180</height>
    <birthday>2024-11-22T00:41:50.823Z</birthday>
  </governor>
</CityRequest>`;

      try {
          const response = await fetch(`http://localhost:9991/api/external/cities`, {
            method: 'POST',
                headers: {
                  'Content-Type': 'application/xml',
                },
                body: requestData,
              });

          if (!response.ok) {
            throw new Error('Network response was not ok');
          }        
          fetchCities();
      } catch (error) {
          console.error("Error fetching cities:", error);
      }
  }; 

  const fetchCities = async () => {
      const requestData = `<?xml version="1.0" encoding="UTF-8"?>
          <CitiesRequest>
          <page>${page + 1}</page>
          <pageSize>${rowsPerPage}</pageSize>
          <SortingStrategy>
          <sortingType>asc</sortingType>
          <sortingColumn>id</sortingColumn>
          </SortingStrategy>
          <FilterStrategy>
          <filterColumn>id</filterColumn>
          <filterType>contains</filterType>
          <filterValue>string</filterValue>
          </FilterStrategy>
          </CitiesRequest>`;

      try {
          const response = await axios.post('http://localhost:9991/api/cities', requestData, {
              headers: {
                  'Content-Type': 'application/xml',
              },
          });

          const parser = new DOMParser();
          const xmlDoc = parser.parseFromString(response.data, "application/xml");

          const ids = xmlDoc.getElementsByTagName("id");
          const n = ids.length;
          setCities([]);
          for (var i = 0; i < n; i++){
            const city: City = {
              id: xmlDoc.getElementsByTagName("id")[i]?.textContent || '',
              name: xmlDoc.getElementsByTagName("name")[i]?.textContent || '',
              coordinates: {
                x: parseFloat(xmlDoc.getElementsByTagName("x")[i]?.textContent || '0'),
                y: parseFloat(xmlDoc.getElementsByTagName("y")[i]?.textContent || '0'),
              },
              creationDate: new Date(xmlDoc.getElementsByTagName("creationDate")[i].textContent || 0),
              area: parseFloat(xmlDoc.getElementsByTagName("area")[i]?.textContent || '0'),
              population: parseInt(xmlDoc.getElementsByTagName("population")[i]?.textContent || '0'),
              metersAboveSeaLevel: parseFloat(xmlDoc.getElementsByTagName("metersAboveSeaLevel")[i]?.textContent || '0'),
              establishmentDate: new Date(xmlDoc.getElementsByTagName("establishmentDate")[i]?.textContent || 0),
              capital: xmlDoc.getElementsByTagName("capital")[i]?.textContent === 'true',
              government: xmlDoc.getElementsByTagName("government")[i]?.textContent || '', 
              governor: {
                height: parseFloat(xmlDoc.getElementsByTagName("height")[i]?.textContent || '0'),
                birthday: new Date(xmlDoc.getElementsByTagName("birthday")[i]?.textContent || 0),
              },
              human: Array.from(xmlDoc.getElementsByTagName("inhabitant")).map(humanElement => ({
                height: parseFloat(humanElement.getElementsByTagName("height")[0]?.textContent || '0'),
                birthday: new Date(humanElement.getElementsByTagName("birthday")[0]?.textContent || 0),
              })),
          }
          setCities((prev) => [...prev, ...[city]]);}
          reverseList(cities);
      } catch (error) {
          console.error("Error fetching cities:", error);
      }
  };

useEffect(() => {  
  fetchCities();
}, []);


  return (
    <div style={{ display: 'flex', flexDirection: 'row', justifyContent: 'space-between' }}>
      <div style={{ marginRight: '10px', marginLeft: '10px'}}>
        <Input text='ID' value={inputValue} onInputChange={handleInputChange} />     
        <ButtonGroup variant="contained" aria-label="Basic button group">
        <GetCityButton inputValue={inputValue} onCityUpdate={handleCityUpdate}/>
          <Stack direction="row" spacing={2}>
            <Button variant="outlined" onClick={handleButtonClick}>Удалить</Button>
          </Stack>
          <Stack direction="row" spacing={2}>
            <Button variant="outlined" onClick={() => {
                                                SetData(inputValue);
                                                setModalActive(true);
                                            }}>Редактировать</Button>
          </Stack>               
        </ButtonGroup>

        <Selects value={selectGovernment} onChange={handleChange}></Selects>
        <GetGovernmentButton inputValue={selectGovernment} onCityUpdate={handleCityUpdateList}></GetGovernmentButton>

        <div style={{marginTop: 10}}>
          <table>
            <td>
             <GetMinIdCityButton onCityUpdate={handleCityUpdate}></GetMinIdCityButton>
            </td>
            <td>
              <GetMaxDateCityButton onCityUpdate={handleCityUpdate}></GetMaxDateCityButton>  
            </td>
          </table>   
        </div>
