import * as React from 'react';
import Paper from '@mui/material/Paper';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TablePagination from '@mui/material/TablePagination';
import TableRow from '@mui/material/TableRow';
import axios from 'axios';
import {useEffect, useState} from 'react';
import Input from "../Components/Input";
import GetCityButton from "./GetCityButton";
import { City } from '../models/City'; 
import { Government } from '../models/Government'; 
import { Coordinates } from '../models/Coordinates';
import { Human } from '../models/Human';
import Button from '@mui/material/Button';
import Stack from '@mui/material/Stack';
import Modal from '../Modal/modal';
import PutCityButton from './PutCityButton';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemButton from '@mui/material/ListItemButton';
interface Column {
  id: keyof City;  
  label: string;
  minWidth?: number;
  align?: 'right';
  format?: (value: number) => string;
}

const columns: readonly Column[] = [
  { id: 'id',
    label: 'ID', 
    minWidth: 50, 
    align: 'right',
    format: (value: number) => value.toLocaleString('en-US'),
  },
  { id: 'name',
    label: 'Name', 
    minWidth: 170, 
    align: 'right'
  },
  {
    id: 'area',
    label: 'Area',
    minWidth: 170,
    align: 'right',
    format: (value: number) => value.toLocaleString('en-US'),
  },
  {
    id: 'coordinates',
    label: 'Coordinates',
    minWidth: 170,
    align: 'right',
    format: (value: number) => value.toLocaleString('en-US'),
  },
  {
    id: 'population',
    label: 'Population',
    minWidth: 170,
    align: 'right',
    format: (value: number) => value.toLocaleString('en-US'),
  },
  {
    id: 'metersAboveSeaLevel',
    label: 'Meters Above Sea Level',
    minWidth: 170,
    align: 'right',
    format: (value: number) => value.toLocaleString('en-US'),
  },
  {
    id: 'government',
    label: 'Government',
    minWidth: 170,
    align: 'right',
  },
  {
    id: 'governor',
    label: 'Governor',
    minWidth: 170,
    align: 'right',
  },
];


const formatValue = (value: any) => {
    if (value === null || value === undefined) {
        return 'N/A'; // Обработка null и undefined
    }

    if (typeof value === 'number') {
        return value.toLocaleString('en-US'); // Форматирование чисел
    } else if (typeof value === 'boolean') {
        return value ? 'Yes' : 'No'; // Преобразование булевых значений
    } else if (value instanceof Date) {
        return value.toLocaleDateString(); // Форматирование дат
    } else if (typeof value === 'object') {
        // Пример для Coordinates
        if ('x' in value && 'y' in value) {
            return `(${value.x ?? 0}, ${value.y})`; // Формат для Coordinates
        }
        // Пример для Human
        if ('height' in value) {
            return `${value.height ?? 'N/A'} cm`; // Формат для Human
        }
        return JSON.stringify(value); // Превратите объекты в строки
    }

    return String(value); // Преобразование других типов в строку
};

const TableCity: React.FC = () => {
  const [cities, setCities] = useState<City[]>([]);
  const [page, setPage] = React.useState(0);
  const [rowsPerPage, setRowsPerPage] = React.useState(10);
  const [inputValue, setInputValue] = useState("");
  const [modalActive, setModalActive] = useState(false);
  const [name, setName] = useState('');
  const [coordinates, setCoordinates] = useState<Coordinates[]>([]);
  const [creationDate, setCreationDatee] = useState('');
  const [area, setArea] = useState('');
  const [population, setPopulation] = useState('');
  const [metersAboveSeaLevel, setMetersAboveSeaLevel] = useState('');
  const [establishmentDate, setEstablishmentDate] = useState('');
  const [capital, setCapital] = useState('');
  const [government, setGovernment] = useState<Government | null>(null);
  const [governor, setGovernor] = useState<Human[]>([]);
  const [x, setX] = useState('');
  const [y, setY] = useState('');
  const [height, setHeight] = useState('');
  const [birthday, setBirthday] = useState('');
  const handleCityUpdate = (newCity: City) => {
    setCities([newCity]); // Устанавливаем состояние, содержащим только новый город
  };

  const handleChangePage = (event: unknown, newPage: number) => {
    setPage(newPage);
  };

  const handleChangeRowsPerPage = (event: React.ChangeEvent<HTMLInputElement>) => {
    setRowsPerPage(+event.target.value);
    setPage(0);
  };


  const handleInputChange = (value: string) => {
      setInputValue(value);
  };
  const handleButtonClick = async () => {
    try {
      const response = await fetch(`http://localhost:9991/api/city/${inputValue}`, {
        method: 'DELETE', // Указываем метод DELETE
      });

      if (response.ok) {
        console.log('City successfully deleted');
        fetchCities();
      } else if (response.status === 400) {
        console.error("Can't find city with such id");
      } else if (response.status >= 500) {
        console.error('Internal server error');
      }
    } catch (error) {
      console.error('Error deleting city:', error);
    }
  };

  const fetchCities = async () => {
    const requestData = `<?xml version="1.0" encoding="UTF-8"?>
<CitiesRequest>
<page>${page + 1}</page>
<pageSize>${rowsPerPage}</pageSize>
<SortingStrategy>
<sortingType>asc</sortingType>
<sortingColumn>id</sortingColumn>
</SortingStrategy>
<FilterStrategy>
<filterColumn>id</filterColumn>
<filterType>contains</filterType>
<filterValue>string</filterValue>
</FilterStrategy>
</CitiesRequest>`;

    try {
        const response = await axios.post('http://localhost:9991/api/city', requestData, {
            headers: {
                'Content-Type': 'application/xml',
            },
        });

        const parser = new DOMParser();
        const xmlDoc = parser.parseFromString(response.data, "application/xml");
        const fetchedCities: City[] = Array.from(xmlDoc.getElementsByTagName('cities')).map(cityElement => ({
            id: cityElement.getElementsByTagName('id')[0].textContent || '',
            name: cityElement.getElementsByTagName('name')[0].textContent || '',
            coordinates: {
                x: Number(cityElement.getElementsByTagName('x')[0]?.textContent) || 0, // Значение по умолчанию
                y: Number(cityElement.getElementsByTagName('y')[0]?.textContent) || 0, // Значение по умолчанию
            },
            creationDate: new Date(cityElement.getElementsByTagName('creationDate')[0]?.textContent || 0), // установка значения по умолчанию
            area: Number(cityElement.getElementsByTagName('area')[0]?.textContent) || 0, // Значение по умолчанию
            population: Number(cityElement.getElementsByTagName('population')[0]?.textContent) || 0, // Значение по умолчанию
            metersAboveSeaLevel: Number(cityElement.getElementsByTagName('metersAboveSeaLevel')[0]?.textContent) || 0, // Значение по умолчанию
            capital: cityElement.getElementsByTagName('capital')[0]?.textContent === 'true',
            government: cityElement.getElementsByTagName('government')[0]?.textContent as Government || Government.DESPOTISM, // Значение по умолчанию
            governor: {
                height: Number(cityElement.getElementsByTagName('Human')[0]?.getElementsByTagName('height')[0]?.textContent) || 0,
                birthday: new Date(cityElement.getElementsByTagName('Human')[0]?.getElementsByTagName('birthday')[0]?.textContent || 0), // установка значения по умолчанию
            },
        }));


        setCities(fetchedCities);
    } catch (error) {
        console.error("Error fetching cities:", error);
    }
};

  useEffect(() => {  
    fetchCities();
}, [page, rowsPerPage]);

  return (
    <div>
      <Input text='ID' value={inputValue} onInputChange={handleInputChange} />
      <GetCityButton inputValue={inputValue} onCityUpdate={handleCityUpdate}/>
      <Stack direction="row" spacing={2}>
        <Button variant="outlined" onClick={handleButtonClick}>Удалить город</Button>
      </Stack>
      <Stack direction="row" spacing={2}>
        <Button variant="outlined" onClick={() => {
                                            setModalActive(true);
                                        }}>Редактировать город</Button>
      </Stack>
        <Paper sx={{ width: '100%', overflow: 'hidden' }}>
        <TableContainer sx={{ maxHeight: 440 }}>
          <Table stickyHeader aria-label="sticky table">
            <TableHead>
              <TableRow>
                {columns.map((column) => (
                  <TableCell
                    key={column.id}
                    align={column.align}
                    style={{ minWidth: column.minWidth }}
                  >
                    {column.label}
                  </TableCell>
                ))}
              </TableRow>
            </TableHead>
            <TableBody>
              {cities
                  .slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
                  .map((city) => {
                      return (
                          <TableRow hover role="checkbox" tabIndex={-1} key={city.id}>
                              {columns.map((column) => {
                                  const value = city[column.id];
                                  return (
                                      <TableCell key={column.id} align={column.align}>
                                          {formatValue(value)}
                                      </TableCell>
                                  );
                              })}
                          </TableRow>
                      );
                  })}
          </TableBody>
          </Table>
        </TableContainer>
        <TablePagination
          rowsPerPageOptions={[1, 2, 3]}
          component="div"
          count={cities.length}
          rowsPerPage={rowsPerPage}
          page={page}
          onPageChange={handleChangePage}
          onRowsPerPageChange={handleChangeRowsPerPage}
        />
      </Paper>
      { <Modal active={modalActive} setActive={setModalActive}>
        <div>
            <List>
              <ListItem>
                <ListItemButton>
                  <Input text='Name' value={name} onInputChange={handleInputChange} />
                  <Input text='Area' value={area} onInputChange={handleInputChange} />
                  <Input text='X' value={x} onInputChange={handleInputChange} />
                  <Input text='Y' value={y} onInputChange={handleInputChange} />
                </ListItemButton>
              </ListItem>
            </List>
            <List>
              <ListItem>
                <ListItemButton>
                  <Input text='Population' value={population} onInputChange={handleInputChange} />
                  <Input text='Meters Above Sea Level' value={metersAboveSeaLevel} onInputChange={handleInputChange} />
                  <Input text='Governor Birthday' value={birthday} onInputChange={handleInputChange} />
                  <Input text='Governor Height' value={height} onInputChange={handleInputChange} />
                </ListItemButton>
              </ListItem>
            </List>
          <PutCityButton inputValue={inputValue} onCityUpdate={handleCityUpdate}></PutCityButton>
        </div>
    </Modal>}
    </div>


  );
}
export default TableCity;
