import * as React from 'react';
import Paper from '@mui/material/Paper';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TablePagination from '@mui/material/TablePagination';
import TableRow from '@mui/material/TableRow';
import axios from 'axios';
import {useEffect, useState} from 'react';
import Input from "../Components/Input";
import GetCityButton from "./GetCityButton";
import { City } from '../models/City'; 
//import { Government } from '../models/Government'; 
//import { Coordinates } from '../models/Coordinates';
//import { Human } from '../models/Human';
import Button from '@mui/material/Button';
import Stack from '@mui/material/Stack';
import Modal from '../Modal/modal';
import GetMinIdCityButton from './GetMinIdCityButton';
import GetMaxDateCityButton from './GetMaxDateCityButton';
import GetGovernmentButton from './GetGovernmentButton';
import Selects from './Selects';
import ButtonGroup from '@mui/material/ButtonGroup';
import '../App.css';
import { relative } from 'path';


interface Column {
  id: keyof City;  
  label: string;
  minWidth?: number;
  align?: 'left';
  format?: (value: number) => string;
}

const columns: readonly Column[] = [
  { id: 'id',
    label: 'ID', 
    minWidth: 20, 
    align: 'left',
    format: (value: number) => value.toLocaleString('en-US'),
  },
  { id: 'name',
    label: 'Name', 
    minWidth: 60, 
    align: 'left'
  },
  {
    id: 'area',
    label: 'Area',
    minWidth: 50,
    align: 'left',
    format: (value: number) => value.toLocaleString('en-US'),
  },
  {
    id: 'coordinates',
    label: 'Coordinates',
    minWidth: 100,
    align: 'left',
    format: (value: number) => value.toLocaleString('en-US'),
  },
  {
    id: 'population',
    label: 'Population',
    minWidth: 60,
    align: 'left',
    format: (value: number) => value.toLocaleString('en-US'),
  },
  {
    id: 'capital',
    label: 'Capital',
    minWidth: 40,
    align: 'left',
  },

  {
    id: 'metersAboveSeaLevel',
    label: 'Sea Level',
    minWidth: 40,
    align: 'left',
    format: (value: number) => value.toLocaleString('en-US'),
  },
  {
    id: 'government',
    label: 'Government',
    minWidth: 70,
    align: 'left',
  },
  {
    id: 'governor',
    label: 'Governor',
    minWidth: 30,
    align: 'left',
  },
  {
    id: 'creationDate',
    label: 'Creation',
    minWidth: 70,
    align: 'left',
  },
  {
    id: 'establishmentDate',
    label: 'Establishment',
    minWidth: 70,
    align: 'left',
  },
];


const formatValue = (value: any) => {
    if (value === null || value === undefined) {
        return 'N/A'; 
    }

    if (typeof value === 'number') {
        return value.toLocaleString('en-US'); 
    } else if (typeof value === 'boolean') {
        return value ? 'Yes' : 'No'; 
    } else if (value instanceof Date) {
        return value.toLocaleDateString(); 
    } else if (typeof value === 'object') {
        if ('x' in value && 'y' in value) {
            return `(${value.x ?? 0}, ${value.y})`; 
        }
        if ('height' in value) {
            return `${value.height ?? 'N/A'} cm`; 
        }
        return JSON.stringify(value); 
    }   
    return String(value); 
};

const TableCity: React.FC = () => {
  const [cities, setCities] = useState<City[]>([]);
  const [page, setPage] = React.useState(0);
  const [rowsPerPage, setRowsPerPage] = React.useState(10);
  const [inputValue, setInputValue] = useState("");
  const [modalActive, setModalActive] = useState(false);
  const [id, setID] = useState('');
  const [name, setName] = useState('');
  //const [coordinates, setCoordinates] = useState<Coordinates[]>([]);
  const [creationDate, setCreationDatee] = useState(new Date());
  const [area, setArea] = useState(Number);
  const [population, setPopulation] = useState(Number);
  const [metersAboveSeaLevel, setMetersAboveSeaLevel] = useState(Number);
  const [establishmentDate, setEstablishmentDate] = useState<Date | null>(null);
  const [capital, setCapital] = useState(Boolean);
  const [government, setGovernment] = useState("");
  //const [governor, setGovernor] = useState<Human[]>([]);
  const [x, setX] = useState(Number);
  const [y, setY] = useState(Number);
  const [height, setHeight] = useState(Number);
  const [birthday, setBirthday] = useState<Date | null>(null);

  const handleCityUpdate = (newCity: City) => {
    setCities([newCity]);
  };

  const handleChangePage = (event: unknown, newPage: number) => {
    setPage(newPage);
  };

  const handleChangeRowsPerPage = (event: React.ChangeEvent<HTMLInputElement>) => {
    setRowsPerPage(+event.target.value);
    setPage(0);
  };


  const handleInputChange = (value: string) => {
      setInputValue(value);
  };

  const SetData = (value: string) => {
    const index = Number(value) - 1; 
    if (index >= 0 && index < cities.length) {
        const selectedCity = cities[index];
        setID(inputValue);
        setName(selectedCity.name);
        setArea(selectedCity.area);
        setX(selectedCity.coordinates.x ?? 0);
        setY(selectedCity.coordinates.y);
        setPopulation(selectedCity.population ?? 0);
        setCapital(selectedCity.capital);
        setMetersAboveSeaLevel(selectedCity.metersAboveSeaLevel ?? 0);
        setHeight(selectedCity.governor.height ?? 0);
        setCreationDatee(new Date(selectedCity.creationDate));
        setEstablishmentDate(selectedCity.establishmentDate ?? null);
        setBirthday(selectedCity.governor.birthday ?? null);
        setGovernment(selectedCity.government);
    }};


  const handleButtonClick = async () => {
    try {
      const response = await fetch(`http://localhost:9991/api/city/${inputValue}`, {
        method: 'DELETE',

      });

      if (response.ok) {
        console.log('City successfully deleted');
        fetchCities();
      } else if (response.status === 400) {
        console.error("Can't find city with such id");
      } else if (response.status >= 500) {
        console.error('Internal server error');
      }
    } catch (error) {
      console.error('Error deleting city:', error);
    }
  };


  const fetchUpdateCity = async () => {
    const requestData = `<?xml version="1.0" encoding="UTF-8"?>
        <City>
          <id>${id}</id>
          <name>${name}</name>
          <Coordinates>
            <x>${x}</x>
            <y>${y}</y>
          </Coordinates>
          <creationDate>${creationDate}</creationDate>
          <area>${area}</area>
          <population>${population}</population>
          <metersAboveSeaLevel>${metersAboveSeaLevel}</metersAboveSeaLevel>
          <establishmentDate>${establishmentDate}</establishmentDate>
          <capital>${capital}</capital>
          <government>${government}</government>
          <Human>
            <height>${height}</height>
            <birthday>${birthday}</birthday>
          </Human>
        </City>`;

        try {
          const response = await fetch(`http://localhost:9991/api/city`, {
            method: 'PUT',
                headers: {
                  'Content-Type': 'application/xml',
                },
                body: requestData,
              });

              if (!response.ok) {
                throw new Error('Network response was not ok');
              }        
              fetchCities();
      } catch (error) {
          console.error("Error fetching cities:", error);
      }
    }

  const fetchCities = async () => {
    const requestData = `<?xml version="1.0" encoding="UTF-8"?>
        <CitiesRequest>
        <page>${page + 1}</page>
        <pageSize>${rowsPerPage}</pageSize>
        <SortingStrategy>
        <sortingType>asc</sortingType>
        <sortingColumn>id</sortingColumn>
        </SortingStrategy>
        <FilterStrategy>
        <filterColumn>id</filterColumn>
        <filterType>contains</filterType>
        <filterValue>string</filterValue>
        </FilterStrategy>
        </CitiesRequest>`;

    try {
        const response = await axios.post('http://localhost:9991/api/city', requestData, {
            headers: {
                'Content-Type': 'application/xml',
            },
        });

        const parser = new DOMParser();
        const xmlDoc = parser.parseFromString(response.data, "application/xml");
        const fetchedCities: City[] = Array.from(xmlDoc.getElementsByTagName('cities')).map(cityElement => ({
            id: cityElement.getElementsByTagName('id')[0].textContent || '',
            name: cityElement.getElementsByTagName('name')[0].textContent || '',
            coordinates: {
                x: Number(cityElement.getElementsByTagName('x')[0]?.textContent) || 0, 
                y: Number(cityElement.getElementsByTagName('y')[0]?.textContent) || 0, 
            },
            creationDate: new Date(cityElement.getElementsByTagName('creationDate')[0]?.textContent || 0), 
            area: Number(cityElement.getElementsByTagName('area')[0]?.textContent) || 0, 
            population: Number(cityElement.getElementsByTagName('population')[0]?.textContent) || 0, 
            metersAboveSeaLevel: Number(cityElement.getElementsByTagName('metersAboveSeaLevel')[0]?.textContent) || 0, 
            capital: cityElement.getElementsByTagName('capital')[0]?.textContent === 'true',
            government: cityElement.getElementsByTagName('government')[0].textContent || '', 
            governor: {
                height: Number(cityElement.getElementsByTagName('Human')[0]?.getElementsByTagName('height')[0]?.textContent) || 0,
                birthday: new Date(cityElement.getElementsByTagName('Human')[0]?.getElementsByTagName('birthday')[0]?.textContent || 0), 
            },
        }));
        setCities(fetchedCities);
    } catch (error) {
        console.error("Error fetching cities:", error);
    }
};

useEffect(() => {  
  fetchCities();
}, []);


  return (
    <div style={{ display: 'flex', flexDirection: 'row', justifyContent: 'space-between' }}>
      <div style={{ marginRight: '10px', marginLeft: '10px'}}>
        <Input text='ID' value={inputValue} onInputChange={handleInputChange} />     
        <ButtonGroup variant="contained" aria-label="Basic button group">
        <GetCityButton inputValue={inputValue} onCityUpdate={handleCityUpdate}/>
          <Stack direction="row" spacing={2}>
            <Button variant="outlined" onClick={handleButtonClick}>Удалить</Button>
          </Stack>
          <Stack direction="row" spacing={2}>
            <Button variant="outlined" onClick={() => {
                                                SetData(inputValue);
                                                setModalActive(true);
                                            }}>Редактировать</Button>
          </Stack>               
        </ButtonGroup>

        <Selects></Selects>
        <GetGovernmentButton inputValue={inputValue} onCityUpdate={handleCityUpdate}></GetGovernmentButton>

        <div style={{marginTop: 10}}>
          <ButtonGroup>
            <GetMinIdCityButton onCityUpdate={handleCityUpdate}></GetMinIdCityButton>
            <GetMaxDateCityButton onCityUpdate={handleCityUpdate}></GetMaxDateCityButton>  
          </ButtonGroup>   
        </div>       
      </div>

      <Paper sx={{ width: '100%', overflow: 'hidden' }}>
        <div>
        <Stack direction="row" spacing={2}>
              <Button variant="outlined" onClick={() => {
                                                  fetchCities();                                            
                                                  }}>Все города</Button>
            </Stack>    
        </div>
          <TableContainer sx={{ maxHeight: 440}}>
            <Table stickyHeader aria-label="sticky table">
              <TableHead sx={{position: relative}}>
                <TableRow >
                  {columns.map((column) => (
                    <TableCell
                      key={column.id}
                      align={column.align}
                      style={{ minWidth: column.minWidth }}
                    >
                      {column.label}
                    </TableCell>
                  ))}
                </TableRow>
              </TableHead>
              <TableBody>
                {cities
                    .slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
                    .map((city) => {
                        return (
                            <TableRow hover role="checkbox" tabIndex={-1} key={city.id}>
                                {columns.map((column) => {
                                    const value = city[column.id];
                                    return (
                                        <TableCell key={column.id} align={column.align}>
                                            {formatValue(value)}
                                        </TableCell>
                                    );
                                })}
                            </TableRow>
                        );
                    })}
            </TableBody>
            </Table>
          </TableContainer>
          <TablePagination
            rowsPerPageOptions={[1, 2, 3]}
            component="div"
            count={cities.length}
            rowsPerPage={rowsPerPage}
            page={page}
            onPageChange={handleChangePage}
            onRowsPerPageChange={handleChangeRowsPerPage}
          />
        </Paper>


      { <Modal active={modalActive} setActive={setModalActive}>
        <div>
            <table className="tableAdd">
              <tr>
                <td><Input text='Name' value={name} onInputChange={(value: string) => setName(value)} /></td>
                <td><Input text='Area' value={String(area)} onInputChange={(value: string) => setArea(Number(value))}  /></td>
                <td><Input text='X' value={String(x)} onInputChange={(value: string) => setX(Number(value))} /></td>
                <td><Input text='Y' value={String(y)} onInputChange={(value: string) => setY(Number(value))} /></td>
              </tr>
              <tr>
                <td><Input text='Population' value={String(population)} onInputChange={(value: string) => setPopulation(Number(value))} /></td>
                <td><Input text='Meters Above Sea Level' value={String(metersAboveSeaLevel)} onInputChange={(value: string) => setMetersAboveSeaLevel(Number(value))}/></td>
                <td><Input text='Governor Height' value={String(height)} onInputChange={(value: string) => setHeight(Number(value))} /></td>
                <td><Selects></Selects></td>
              </tr>

            </table>
            <table className="tableAdd">
              <tr>
                  <td></td>
                  <td><Button variant="outlined" onClick={fetchUpdateCity}>Изменить</Button></td>
                  <td></td>
                </tr>
            </table>
        </div>
    </Modal>}
    </div>    

  );
}
export default TableCity;
